import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProducts } from '../features/products/ProductSlice';
import ProductCard from './ProductCard';

const ProductList = () => {
  const dispatch = useDispatch();
  const { items, status } = useSelector((state) => state.products);
  const [displayedProducts, setDisplayedProducts] = useState([]);
  const [loadCount, setLoadCount] = useState(8);

  useEffect(() => {
    dispatch(fetchProducts(loadCount));
  }, [dispatch, loadCount]);

  useEffect(() => {
    setDisplayedProducts(items);
  }, [items]);

  const handleLoadProducts = (count) => {
    setLoadCount(count);
  };

  if (status === 'loading') {
    return <p>Loading...</p>;
  }

  if (status === 'failed') {
    return <p>Error loading products.</p>;
  }

  return (
    <div>
      <div className="flex justify-center space-x-4 mb-4">

        <button className="bg-indigo-700 rounded-md text-white px-5 py-1" onClick={() => handleLoadProducts(8)}>Load 8 products</button>
        <button onClick={() => handleLoadProducts(16)}>Load 16 products</button>
        <button onClick={() => handleLoadProducts(20)}>Load all products</button>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {displayedProducts.map((product) => (
            <ProductCard product={product} />
        ))}
      </div>
    </div>
  );
}; 

export default ProductList;
