import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('/api/products?limit=8');
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const loadProducts = async (limit) => {
    try {
      const response = await axios.get(`/api/products?limit=${limit}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  return (
    <div>
      <h1>Products</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {loading ? (
          <p>Loading...</p>
        ) : (
          products.map((product) => (
            <div key={product.id} className="border p-4">
              <Link to={`/products/${product.id}`}>
                <img src={product.image} alt={product.name} className="mb-2" />
                <h2 className="text-xl font-bold">{product.name}</h2>
                <p className="text-gray-600">${product.price}</p>
              </Link>
            </div>
          ))
        )}
      </div>
      <div>
        <button onClick={() => loadProducts(8)}>Load 8 Products</button>
        <button onClick={() => loadProducts(16)}>Load 16 Products</button>
        <button onClick={() => loadProducts('all')}>Load All Products</button>
      </div>
    </div>
  );
};

export default ProductsPage;
