import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProducts } from '../features/products/ProductSlice';
import ProductCard from './ProductCard';
import { Link } from 'react-router-dom';

const ProductList = () => {
  const dispatch = useDispatch();
  const { items, status } = useSelector((state) => state.products);
  const [loadCount, setLoadCount] = useState(8); // Состояние для хранения количества загружаемых товаров

  useEffect(() => {
    dispatch(fetchProducts(loadCount)); // Используем текущее значение loadCount при загрузке
  }, [dispatch, loadCount]); // Добавляем loadCount в зависимости useEffect

  const handleLoadProducts = (count) => {
    setLoadCount(count); // Устанавливаем новое значение loadCount при нажатии на кнопку
  };

  if (status === 'loading') {
    return <p>Loading...</p>;
  }

  if (status === 'failed') {
    return <p>Error loading products.</p>;
  }

  return (
    <div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {items.map((product) => (
          <Link key={product.id} to={`/products/${product.id}`}>
            {/* Оборачиваем карточки товаров в ссылку для перехода на страницу продукта */}
            <ProductCard product={product} />
          </Link>
        ))}
      </div>
    </div>
  );
}; 

export default ProductList;
