import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchProducts = createAsyncThunk('products/fetchProducts', async (count = 8) => {
  const response = await axios.get(`https://fakestoreapi.com/products?limit=${count}`);
  return response.data;
});

export const createProduct = createAsyncThunk('products/createProduct', async (newProduct) => {
  const response = await axios.post('https://fakestoreapi.com/products', newProduct);
  return response.data;
});

// аналогичные thunk для updateProduct и deleteProduct
