import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // добавляем импорт useParams

const ProductDetailPage = () => {
  const { id } = useParams(); // получаем параметр id из URL
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`https://fakestoreapi.com/products/${id}`);
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id]); // добавляем id в зависимости useEffect

  return (
    <div  className='flex justify-center'>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className='p-10 flex flex-col w-100 justify-center border rounded-md shadow-md'>
            <img src={product.image} alt={product.title} className="h-40  w-21 object-cover mb-10" />
            <h2 className="text-lg font-bold mb-3">{product.title}</h2>
            <p className="text-gray-600 mb-2">Price: ${product.price}</p>
            <p className="mb-2">Description: {product.description}</p>
            <p className="mb-2">Category: {product.category}</p>
        </div>
      )}
    </div>
  );
};

export default ProductDetailPage;
