import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const ProductForm = ({ onSubmit }) => {
  const formik = useFormik({
    initialValues: {
      title: '',
      price: '',
      description: '',
      published: false,
    },
    validationSchema: Yup.object({
      title: Yup.string().required('Required'),
      price: Yup.number().required('Required').positive('Must be positive'),
      description: Yup.string().required('Required'),
    }),
    onSubmit: (values) => {
      onSubmit(values);
    },
  });

  return (
    <form onSubmit={formik.handleSubmit} className="max-w-md mx-auto p-4 border">
      <div className="mb-4">
        <label className="block mb-1">Title</label>
        <input
          type="text"
          name="title"
          onChange={formik.handleChange}
          value={formik.values.title}
          className="w-full border p-2"
        />
        {formik.touched.title && formik.errors.title ? <div className="text-red-500">{formik.errors.title}</div> : null}
      </div>
      <div className="mb-4">
        <label className="block mb-1">Price</label>
        <input
          type="text"
          name="price"
          onChange={formik.handleChange}
          value={formik.values.price}
          className="w-full border p-2"
        />
        {formik.touched.price && formik.errors.price ? <div className="text-red-500">{formik.errors.price}</div> : null}
      </div>
      <div className="mb-4">
        <label className="block mb-1">Description</label>
        <textarea
          name="description"
          onChange={formik.handleChange}
          value={formik.values.description}
          className="w-full border p-2"
        />
        {formik.touched.description && formik.errors.description ? <div className="text-red-500">{formik.errors.description}</div> : null}
      </div>
      <div className="mb-4">
        <label className="block mb-1">Published</label>
        <input
          type="checkbox"
          name="published"
          onChange={formik.handleChange}
          checked={formik.values.published}
        />
      </div>
      <button type="submit" className="bg-blue-500 text-white py-2 px-4">Submit</button>
    </form>
  );
};

export default ProductForm;
