import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams, useHistory } from 'react-router-dom';
import { fetchProducts, updateProduct } from '../features/products/productsSlice';
import ProductForm from '../components/ProductForm';

const EditProduct = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const history = useHistory();
  const product = useSelector((state) => state.products.products.find(p => p.id === Number(id)));

  useEffect(() => {
    if (!product) {
      dispatch(fetchProducts());
    }
  }, [product, dispatch, id]);

  const handleEditProduct = (updatedProduct) => {
    dispatch(updateProduct({ ...updatedProduct, id: Number(id) }));
    history.push('/products');
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Edit Product</h1>
      {product ? (
        <ProductForm onSubmit={handleEditProduct} initialValues={product} />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default EditProduct;
