import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchProducts = createAsyncThunk('products/fetchProducts', async () => {
  const response = await axios.get('https://fakestoreapi.com/products');
  return response.data;
});

const productsSlice = createSlice({
  name: 'products',
  initialState: {
    apiProducts: [],
    addedProducts: [],
    status: 'idle',
    error: null,
  },
  reducers: {
    addProduct: (state, action) => {
      state.addedProducts.push(action.payload);
    },
    editProduct: (state, action) => {
      const index = state.addedProducts.findIndex(product => product.id === action.payload.id);
      if (index !== -1) {
        state.addedProducts[index] = action.payload;
      }
    },
    deleteProduct: (state, action) => {
      state.addedProducts = state.addedProducts.filter(product => product.id !== action.payload);
    }
  },
  extraReducers: {
    [fetchProducts.pending]: (state) => {
      state.status = 'loading';
    },
    [fetchProducts.fulfilled]: (state, action) => {
      state.status = 'succeeded';
      state.apiProducts = action.payload;
    },
    [fetchProducts.rejected]: (state, action) => {
      state.status = 'failed';
      state.error = action.error.message;
    }
  }
});

export const { addProduct, editProduct, deleteProduct } = productsSlice.actions;

export default productsSlice.reducer;
